resources.string:
query {
  search(type: REPOSITORY, query: "topic:android sort:stars-desc", first:%1$s, after:%2$s) {
    repositoryCount
    pageInfo {
      endCursor
      startCursor
    }

    nodes {
      ... on Repository {
          id
          name
          description
          url
          updatedAt
          forkCount

        	owner {
            login
            avatarUrl
          }

          stargazers {
            totalCount
          }

          repositoryTopics(first:5) {
            totalCount
            nodes {
              resourcePath
            }
          }

          languages(first:1, orderBy: {field:SIZE, direction:DESC}) {
            totalCount
            nodes {
              name
              color
            }
          }
      }
    }
  }
}
